-- Show counts
SELECT
  COUNT(*) FILTER (WHERE question_key LIKE 'META-%') as metadata,
  COUNT(*) FILTER (WHERE question_key LIKE 'STAB-%') as stabilize,
  COUNT(*) FILTER (WHERE question_key LIKE 'ORG-%') as organize,
  COUNT(*) FILTER (WHERE question_key LIKE 'BUILD-%') as build,
  COUNT(*) FILTER (WHERE question_key LIKE 'GROW-%') as grow,
  COUNT(*) FILTER (WHERE question_key LIKE 'SYS-%') as systemic,
  COUNT(*) FILTER (WHERE question_key LIKE 'DISC-%') as disc,
  COUNT(*) as total
FROM questions;

